import axios from "axios";
import * as constants from "../actions/types"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const headers = {
  'Accept': 'application/json',
  'Content-Type': 'application/json'
}

export const userRegistration = (rgData) => async (dispatch) => {
  dispatch({
    type: constants.REGISTER_REQUEST
  });

  try {
    await axios.post('api/signup',rgData,{headers: headers})
    .then((res)=>{
      console.log(res.response);
    });
  } catch (error) {
    dispatch({
      type: constants.REGISTER_ERROR
    });
    console.log(error.response);
    var tempErr = []
    Object.keys(error.response.data.errors).forEach(function(key,inde) {
        console.log(key);
        console.log(inde);
        tempErr.push(key[inde]);
    });
    console.log(tempErr);
    // if(error.response.data.errors.password[0]){
    //   toast.error(error.response.data.errors.password[0], {
    //     position: toast.POSITION.TOP_CENTER
    //   });
    // } else if(error.response.data.errors.email[0]){
    //   toast.error(error.response.data.errors.email[0], {
    //     position: toast.POSITION.TOP_CENTER
    //   });
    // }
  }

  console.log(rgData);
}
