import { useState } from "react";

const Pagination = ({
	data = [],
	handlePagination,
	size=5,
	...props
}) => {
	const dataLimit = (props && props.dataLimit) || 1;
	const [pages] = useState(Math.round(data.length / dataLimit));
	var totalEntries = data.length;
	const totalCount = (pages * dataLimit) >= data.length ? pages : (pages + 1);
	const [pageSize, setPageSize] = useState(size);
	const [curPage, setCurPage] = useState(1);
	const [startIndex, setStartIndex] = useState(1);
	const [endIndex, setEndIndex] = useState(dataLimit);
	const enableFirBtn = curPage > 1 ? false : "disabled";
	const enableLastBtn = curPage === totalEntries ? "disabled" : false;
	const enablePreBtn = curPage > 1 ? false : "disabled";
	const enableNextBtn = (totalCount > 1 && curPage < totalCount) ? false : "disabled";
	//For mobile section 28-09-2021
	var pageNumbers = [];
	for (let i = 1; i <= totalCount; i++) {
		pageNumbers.push(i)
	}
	var curPageNumbers = pageNumbers.slice((curPage - 1), pageSize);
	var curPageSize = curPageNumbers.length;
	if (curPageSize < size) {
		var actPageNumber = (curPage - (size - curPageSize));
		curPageNumbers = pageNumbers.slice((actPageNumber - 1), pageSize);
	}

	const items = []
	curPageNumbers.forEach(res => {
		var curTab = curPage === res ? "active" : "";
		items.push(
			<li className={`page-item ${curTab}`}>
				<h6
					className="page-link"
					href="#"
					style={{ cursor: "pointer", boxSizing: "border-box" }}
					onClick={(eve) => { handleCurrentData(res) }}
				>
					{res}
				</h6>
			</li>
		)
	})

	const handleCurrentData = (currentPage) => {
		setCurPage(currentPage)
		var curPageCount = currentPage * dataLimit;
		var pageStart = currentPage === 1 ? 0 : (currentPage - 1);
		var startIndex = currentPage === 1 ? 0 : ((pageStart * dataLimit));
		var endIndex = curPageCount <= totalEntries ? curPageCount : totalEntries;
		setStartIndex(startIndex === 0 ? 1 : (startIndex + 1))
		setEndIndex(endIndex)
		setPageSize(size + pageStart);
		// handlePagination(currentPage, startIndex, endIndex)
	}


	return (
		<div className="row">
				<div className="col-md-4">
					<h6 className="text-bold">Showing {startIndex} to {endIndex} of {totalEntries} Entries</h6>
				</div>
				{
					<div className={`col-md`}>
						<nav aria-label="...">
							<ul className="pagination">
								<li className={`page-item ${enableFirBtn}`}>
									<h6
										className="page-link"
										style={{ cursor: "pointer" }}
										onClick={(e) => handleCurrentData(1)}
									>
										First
									</h6>
								</li>
								<li className={`page-item ${enablePreBtn}`}>
									<h6
										className="page-link"
										style={{ cursor: "pointer" }}
										onClick={(e) => handleCurrentData(curPage - 1)}
									>
										{"<"}
									</h6>
								</li>
								{
									items
								}
								<li className={`page-item ${enableNextBtn}`}>
									<h6
										className="page-link"
										style={{ cursor: "pointer" }}
										onClick={(e) => handleCurrentData(curPage + 1)}
									>
										{">"}
									</h6>
								</li>
								<li className={`page-item ${enableLastBtn}`}>
									<h6
										className="page-link"
										style={{ cursor: "pointer" }}
										onClick={(e) => handleCurrentData(totalCount)}
									>
										Last
									</h6>
								</li>
							</ul>
						</nav>
					</div>
				}
			</div>
	)
}

export default Pagination;
