import React, { useEffect, useState, useRef } from "react";
import { useSelector,useDispatch } from "react-redux";
import {listCatByProduct} from "../actions/products";
import CLoader from "./Layouts/CLoader";
import ReactPaginate from "react-paginate";
const ProductByCategory = (props) => {
  const dispatch = useDispatch();
  const pData = useSelector(state => state.productsApi);

  // We start with an empty list of items.
  const [currentItems, setCurrentItems] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  // Here we use item offsets; we could also use page offsets
  // following the API or data you're working with.
  const [itemOffset, setItemOffset] = useState(0);

  useEffect(()=>{
    const fData = {
      'id':atob(props?.match?.params?.id)
    }
    dispatch(listCatByProduct(fData));
    const endOffset = itemOffset + itemsPerPage;

    console.log(`Loading items from ${itemOffset} to ${endOffset}`);

    setCurrentItems(pData?.productsData?.data?.data.slice(itemOffset, endOffset));
    setPageCount(Math.ceil(pData?.productsData?.data?.data.length / itemsPerPage));

  },[itemOffset, itemsPerPage]);

  const changePage = (evt) => {
    //console.log(evt);
    const newOffset = (evt.selected * itemsPerPage) % items.length;
    let fData = {
      'id':atob(props?.match?.params?.id),
      'selected':newOffset
    }
    // console.log(fData);
    dispatch(listCatByProduct(fData));
    setItemOffset(newOffset);
  }
  const products = (pData?.productsData?.data?.data === undefined) ? [] : pData?.productsData?.data?.data;

  // console.log(pData);
  return (
    <div className="container mx-auto" style={{marginTop:'20px'}}>
      {
        pData.isLoading == true ? <CLoader /> :
          <div className="grid grid-cols-4 gap-4">
            {
              (products?.length <= 0 || products?.length === undefined) ? <img src="https://i.pinimg.com/originals/c9/22/68/c92268d92cf2dbf96e3195683d9e14fb.png"/>: 
                products.map((product) => {
                  return (
                    <div className="max-w-sm rounded overflow-hidden shadow-lg" key={product.id}>
                      <img className="w-full" src={product?.pimage?.img} alt={product.pname} loading="lazy"/>
                      <div className="px-6 py-4">
                        <div className="font-bold text-xl mb-2">{product.pname}</div>
                        <p className="text-gray-700 text-base float-right">Rs {product.p_amt} /-</p>
                        <p className="text-gray-700 text-base float-left">{product.pdesc} </p>
                      </div>
                      <div className="px-6 pt-4 pb-2">
                        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#photography</span>
                        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#travel</span>
                        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">#winter</span>
                      </div>
                      <center>
                      <button className="inline-flex items-center h-8 px-4 m-2 text-sm text-indigo-100 transition-colors duration-150 bg-indigo-700 rounded-lg focus:shadow-outline hover:bg-indigo-800">Add to Cart</button>
                      </center>

                    </div>
                  )
                }
                )
            }
            <ReactPaginate 
              previousLabel={"Previous"}
              nextLabel={"Next"}
              pageCount={pageCount}
              onPageChange={changePage}
              containerClassName={"paginationbtns"}
              previousLinkClassName={"previousbtn"}
              nextLinkClassName={"nextbtn"}
              disabledClassName={"disabledPage"}
              activeClassName={"activePage"}
              renderOnZeroPageCount={null}
            />
      </div>
    }
    </div>
  )
}

export default ProductByCategory;
