import Loader from "react-loader-spinner";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
const CLoader = () => {
  //other logic
  const parentOverlay = {
      position: "fixed",
      top: 0,
      left: 0,
      background: "#666 !important",
      opacity: 0.8,
      zIndex: 998,
      height: '100%',
      width: '100%',
    };

  const overlayBox = {
    position:'absolute',
    top:'50%',
    left:'50%',
    transform:'translate(-50%, -50%)',
    color: 'white',
    background: '#666666',
    opacity: .8,
    zIndex: 1000,
}
  return (
    <div style={parentOverlay} width="100%">
      <div style={overlayBox}>
        <Loader
          type="ThreeDots"
          color="#00BFFF"
          height={100}
          width={100}
        />
      </div>
    </div>
  );
}

export default CLoader
