import {
  PRODUCT_REQUEST,
  PRODUCT_ERROR,
  PRODUCT_SUCCESS,
  VIEW_PRODUCT_REQUEST,
  VIEW_PRODUCT_ERROR,
  VIEW_PRODUCT_SUCCESS,
  CAT_PRODUCT_REQUEST,
  CAT_PRODUCT_ERROR,
  CAT_PRODUCT_SUCCESS,
} from "../actions/types";

const initialState = {
  isLoading:false,
  productsData:null,
  categoryData:null,
  error:"",
  success:"",
};

export default function (state = initialState, action) {
  switch (action.type) {
    case PRODUCT_REQUEST:
      return {
        ...state,
        isLoading: true,
        success:"",
      };
    case PRODUCT_SUCCESS:
      return {
        ...state,
        isLoading: false,
        categoryData:action.payload,
        success:"success",
      };
    case PRODUCT_ERROR:
      return {
        ...state,
        isLoading: false,
        categoryData:null,
        error:"error",
      };

    case VIEW_PRODUCT_REQUEST:
      return {
        ...state,
        isLoading: true,
        success:"",
      };
    case VIEW_PRODUCT_SUCCESS:
      return {
        ...state,
        isLoading: false,
        productsData:action.payload,
        success:"success",
      };
    case VIEW_PRODUCT_ERROR:
      return {
        ...state,
        isLoading: false,
        productsData:null,
        error:"error",
      };

    case CAT_PRODUCT_REQUEST:
      return {
        ...state,
        isLoading: true,
        success:"",
      };
    case CAT_PRODUCT_SUCCESS:
      return {
        ...state,
        isLoading: false,
        productsData:action.payload,
        success:"success",
      };
    case CAT_PRODUCT_ERROR:
      return {
        ...state,
        isLoading: false,
        productsData:null,
        error:"error",
      };
    default:
      return state;
  }
}
