import React, { useEffect, useState, useRef, Fragment } from "react";
import { Menu, Transition } from '@headlessui/react'
import { Link } from "react-router-dom";
import 'font-awesome/css/font-awesome.min.css';
import axios from "axios";
import DataTable from 'react-data-table-component';
import { useFormik } from 'formik';
import * as Yup from 'yup';
const AddProduct = () => {

  const formik = useFormik({
     initialValues: {
       p_name: '',
       p_image_name:'',
       p_description:'',
       available:'',
     },
     validationSchema: Yup.object({
      p_name: Yup.string().required('This field is required'),
      p_image_name: Yup.string().required('This field is required'),
      p_description: Yup.string().required('This field is required'),
    }),
     onSubmit: values => {
       console.log(JSON.stringify(values, null, 2));
     },
   });

  return (
      <div className="container w-full mx-auto pt-20">
        <div className="w-full px-4 md:px-0 md:mt-8 mb-16 text-gray-800 leading-normal">
          <div className="flex flex-row flex-wrap flex-grow mt-2">
            <div className="w-full p-3">
              <div className="bg-white border rounded shadow">
                <div className="border-b p-3">
                  <h5 className="font-bold uppercase text-gray-600">
                    Add Product
                  </h5>
                </div>
                <div className="p-5">
                  <form className="w-full max-w-sm" onSubmit={formik.handleSubmit}>
                    <div className="md:flex md:items-center mb-6 grid grid-cols-4">
                      <div className="md:w-1/3">
                        <label className="block text-500 font-bold md:text-right mb-1 md:mb-0 pr-4" htmlFor="inline-full-name">
                          Product Name
                        </label>
                      </div>
                      <div className="md:w-2/3">
                        <input className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" name="p_name" id="inline-full-name" type="text" onChange={formik.handleChange} value={formik.values.p_name} />
                         {formik.errors.p_name ? <div className="text-red">{formik.errors.p_name}</div> : null}
                      </div>
                    </div>

                    <div className="md:flex md:items-center">
                      <div className="md:w-1/3"></div>
                      <div className="md:w-2/3">
                        <button className="shadow bg-blue-500 hover:bg-blue-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded" type="submit">Submit</button>&nbsp;&nbsp;&nbsp;&nbsp;
                        <button className="shadow bg-red-500 hover:bg-red-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded" type="button">Cancel</button>
                      </div>
                    </div>
                  </form>
                  {/* <p className="py-2"><a href="#">See More issues...</a></p> */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
  )
}
export default AddProduct
